import EmojiEmotionsOutlinedIcon from '@mui/icons-material/EmojiEmotionsOutlined';
import ImageOutlinedIcon from '@mui/icons-material/ImageOutlined';
import { Avatar, Button, CircularProgress, IconButton, Paper, Typography } from '@mui/material';
import * as React from 'react';
import { useDispatch } from 'react-redux';
import { fetchAddTweet } from '../../store/ducks/tweets/actionCreators';
import {
  AddForm,
  AddFormBody,
  AddFormBottom,
  AddFormCircleProgress,
  AddFormFooter,
  AddFormRight,
  AddFormTextarea,
} from './AddTweetForm.styled';

export interface AddTweetFormProps {
  maxRows?: number;
}

const AddTweetForm: React.FC<AddTweetFormProps> = ({
  maxRows,
}: AddTweetFormProps): React.ReactElement => {
  const dispatch = useDispatch();
  const [text, setText] = React.useState<string>('');
  const handleChangeText = (e: React.FormEvent<HTMLTextAreaElement>) => {
    if (e.currentTarget) {
      setText(e.currentTarget.value);
    }
  };
  const handleClickAddButton = () => {
    dispatch(fetchAddTweet(text));
  };

  const MAX_LENGTH = 280;
  const textLimitPercent = Math.floor((text.length / MAX_LENGTH) * 100);
  const textCount = MAX_LENGTH - text.length - 1;

  return (
    <Paper>
      <AddForm>
        <AddFormBody>
          <Avatar
            alt={'User Avatar'}
            src='data:image/jpeg;base64,'
          />
          <AddFormTextarea
            placeholder='What happened?'
            onChange={handleChangeText}
            value={text}
            maxRows={maxRows}
          />
        </AddFormBody>
        <AddFormBottom>
          <AddFormFooter>
            <IconButton>
              <ImageOutlinedIcon />
            </IconButton>
            <IconButton>
              <EmojiEmotionsOutlinedIcon />
            </IconButton>
          </AddFormFooter>

          <AddFormRight>
            {text && (
              <>
                <Typography>{textCount}</Typography>
                <AddFormCircleProgress>
                  <CircularProgress
                    variant='determinate'
                    size={20}
                    thickness={4}
                    value={textLimitPercent}
                  />
                  <CircularProgress
                    style={
                      textLimitPercent < 100 ? { color: 'rgba(0, 0, 0, 0.1)' } : { color: 'red' }
                    }
                    variant='determinate'
                    size={20}
                    thickness={4}
                    value={100}
                  />
                </AddFormCircleProgress>
              </>
            )}
            <Button
              onClick={handleClickAddButton}
              disabled={textLimitPercent >= 100 || text.length === 0}
              color='primary'
              variant='contained'
              fullWidth
              sx={{ height: 30, width: '100%' }}>
              Tweet
            </Button>
          </AddFormRight>
        </AddFormBottom>
      </AddForm>
    </Paper>
  );
};

export default AddTweetForm;
